mapper.select:
SELECT od.ORDER_ID orderId, od.EXORDER_ID exorderId, oa.NOPAY totalPrice, oa.ORDER_TYPE orderType, od.CREATE_DATE createDate, od.STAT stat, os.DEPDAY depDate, os.ARVDAY arrDate FROM t_odm_order od LEFT JOIN T_OMD_ORDER_ATTR oa ON od.ORDER_ID = oa.ORDER_ID LEFT JOIN T_ODM_PASGINFO pasg ON oa.Id = pasg.ORDER_ATTR_ID LEFT JOIN T_ODM_PAYINFO opay ON od.ORDER_ID = opay.ORDER_ID LEFT JOIN T_ODM_PASGSECT os ON pasg.Id = os.PASG_INFO_ID LEFT JOIN T_ODM_FLTINFO of ON os.T_O_ID = of.id WHERE od.MEMBER_ID = #{member} AND od.ORDER_SRC='MZ013'
AND od.STAT=#{stat}
GROUP BY od.ORDER_ID ORDER BY od.EXORDER_ID DESC limit #{pages},#{pieces}

select count(cnt) from(SELECT count(*) as cnt FROM t_odm_order od LEFT JOIN T_OMD_ORDER_ATTR oa ON od.ORDER_ID = oa.ORDER_ID LEFT JOIN T_ODM_PASGINFO pasg ON oa.Id = pasg.ORDER_ATTR_ID LEFT JOIN T_ODM_PAYINFO opay ON od.ORDER_ID = opay.ORDER_ID LEFT JOIN T_ODM_PASGSECT os ON pasg.Id = os.PASG_INFO_ID LEFT JOIN T_ODM_FLTINFO of ON os.T_O_ID = of.id WHERE od.MEMBER_ID = #{member} AND od.ORDER_SRC='MZ013'
AND od.STAT=#{stat}
GROUP BY od.ORDER_ID)as tab_all;
